import { CartActionTypes } from './cart.types'

export const toggleCurrentCart = user => (
    {
        type: CartActionTypes.TOOGLE_CART_HIDDEN,
        payload: user
    }
)

export const addItem = item =>(
    {
        type: CartActionTypes.ADD_ITEM,
        payload: item
    }
)
